const lastUsedTimerName = env.get('LAST_USED_TIMER');

if (!lastUsedTimerName) {
    return msg;
}

const ha = global.get('homeassistant.ha');
const lastUsedTimer = ha.states[lastUsedTimerName];

if (!lastUsedTimer || !('state' in lastUsedTimer)) {
    node.error(`No state found in ${lastUsedTimer}`);
    return;
}

